export default function ProductList() {
  const { addToCart } = useContext(CartContext);
  const { wishlist, addToWishlist, removeFromWishlist } = useContext(WishlistContext);
  const { showToast } = useContext(ToastContext);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [modalProduct, setModalProduct] = useState(null);
  const [wishlistOpen, setWishlistOpen] = useState(false);

  const filteredProducts =
    selectedCategory === "All"
      ? products
      : products.filter((p) => p.category === selectedCategory);

  const isWishlisted = (id) => wishlist.some(item => item.id === id);

  return (
    <>
      <Wishlist open={wishlistOpen} onClose={() => setWishlistOpen(false)} />
      <ProductModal product={modalProduct} onClose={() => setModalProduct(null)} />
      <div className="category-bar">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`category-btn${selectedCategory === cat ? " active" : ""}`}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
      <div className="products">
        {filteredProducts.map((p) => (
          <div
            className="product-card"
            key={p.id}
            style={{ borderTop: '6px solid #6366f1', background: '#f9fafb', position: 'relative' }}
            onClick={() => setModalProduct(p)}
          >
            {p.qty && p.qty < 5 && (
              <span className="low-stock-badge">Low Stock</span>
            )}
            <span
              className="heart-icon"
              style={{ position: 'absolute', top: 10, right: 14, fontSize: 22, cursor: 'pointer', zIndex: 2 }}
              onClick={e => {
                e.stopPropagation();
                if (isWishlisted(p.id)) {
                  removeFromWishlist(p.id);
                  showToast('Removed from wishlist', 'info');
                } else {
                  addToWishlist(p);
                  showToast('Added to wishlist', 'success');
                }
              }}
              title={isWishlisted(p.id) ? 'Remove from wishlist' : 'Add to wishlist'}
            >
              {isWishlisted(p.id) ? '' : ''}
            </span>
            <img
              src={p.image}
              alt={p.name}
              className="product-img"
              style={{ width: "100%", height: 120, objectFit: "contain", borderRadius: 6, marginBottom: 12 }}
            />
            <div className="product-title" title={p.name}>{p.name}</div>
            <div className="product-price">${p.price}</div>
            <button
              className="add-btn"
              style={{ background: '#6366f1' }}
              onClick={e => { e.stopPropagation(); addToCart(p); showToast('Added to cart', 'success'); }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </>
  );
} 